export const CART_INITIAL_STATE = { 
    isCartOpen: false,
    cartItems: [],
    cartCount: 0,
    cartTotal: 0,
  };

  const cartReducer = (state = CART_INITIAL_STATE, action = {}) => {
    const { type, payload } = action;

    switch (type) {
      case CART_ACTION_TYPES.SET_CART_ITEMS:
        return {
          ...state,
          ...payload,
        };
      default:
        return state;
    }
  };
