import { createContext, useReducer } from 'react';
import {createAction} from '../utils/reducer/reducer.utils';


// Add Items

const addCartItem = (cartItems, productToAdd) => {
  const existingCartItem = cartItems.find(
    (cartItem) => cartItem.id === productToAdd.id
  );

  if (existingCartItem) {
    return cartItems.map((cartItem) =>
      cartItem.id === productToAdd.id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem
    );
  }

  return [...cartItems, { ...productToAdd, quantity: 1 }];
};

const addItemToCart = (productToAdd) => {
  const newCartItems = (addCartItem(cartItems, productToAdd));
  updateCartItemsReducer(newCartItems);
};



// Remove Items

const removeCartItem = (cartItems, cartItemToRemove) => {

  const existingCartItem = cartItems.find(
    (cartItem) => cartItem.id === cartItemToRemove.id
  );

  if (existingCartItem.quantity === 1) {
    return cartItems.filter((cartItem) => cartItem.id !== cartItemToRemove.id);
  }

  return cartItems.map((cartItem) =>
    cartItem.id === cartItemToRemove.id
      ? { ...cartItem, quantity: cartItem.quantity - 1 }
      : cartItem
  );
};

const removeItemToCart = (cartItemToRemove) => {
  const newCartItems= (removeCartItem(cartItems, cartItemToRemove));
  updateCartItemsReducer(newCartItems);
};




// Clear Items

const clearCartItem = (cartItems, cartItemToClear) =>
  cartItems.filter((cartItem) => cartItem.id !== cartItemToClear.id);

const clearItemFromCart = (cartItemToClear) => {
  const newCartItems= (clearCartItem(cartItems, cartItemToClear));
  updateCartItemsReducer(newCartItems);
};



// Toggle Cart

const setIsCartOpen = (bool) => {
  dispatch(createAction(CART_ACTION_TYPES.TOGGLE_CART, bool));
};



// Create Context Initial State

export const CartContext = createContext({
  isCartOpen: false,
  setIsCartOpen: () => {},
  cartItems: [],
  addItemToCart: () => {},
  removeItemFromCart: () => {},
  clearItemFromCart: () => {},
  cartCount: 0,
  cartTotal: 0,
});


// Create Reducer Actions

const CART_ACTION_TYPES = {
  ADD_ITEM: 'ADD_ITEM',
  REMOVE_ITEM: 'REMOVE_ITEM',
  CLEAR_ITEM: 'CLEAR_ITEM',
  TOGGLE_CART: 'TOGGLE_CART',
};

// Create Reducer State

const INITIAL_STATE = {
  isCartOpen: false,
  cartItems: [],
  cartCount: 0,
  cartTotal: 0,
};

// Create Reducer Actions

const cartReducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case CART_ACTION_TYPES.ADD_ITEM:
      return {
        ...state,
        ...payload,
      };
    case CART_ACTION_TYPES.REMOVE_ITEM:
      return {
        ...state,
        ...payload,
      };
    case CART_ACTION_TYPES.CLEAR_ITEM:
      return {
        ...state,
        ...payload,
      };
    case CART_ACTION_TYPES.TOGGLE_CART:
      return {
        ...state,
        isCartOpen: payload,
      };

    default:
      throw new Error(`Unhandled type ${type} in cartReducer`);
  }
};

// Create and export Provider

export const CartProvider = ({ children }) => {
  const [{cartItems, isCartOpen, cartCount, cartTotal}, dispatch] = 
    useReducer(cartReducer, INITIAL_STATE);

  const updateCartItemsReducer = (newCartItems) => {
    const newCartCount = newCartItems.reduce(
      (total, cartItem) => total + cartItem.quantity,
      0
    );

    const newCartTotal = newCartItems.reduce(
      (total, cartItem) => total + cartItem.quantity * cartItem.price,
      0
    );

    dispatch (
      createAction(CART_ACTION_TYPES.ADD_ITEM, {
        cartItems: newCartItems,
        cartCount: newCartCount,
        cartTotal: newCartTotal,
      }),
    );
  };

  // Create Context Value

  const value = {
    isCartOpen,
    setIsCartOpen,
    addItemToCart,
    removeItemToCart,
    clearItemFromCart,
    cartItems,
    cartCount,
    cartTotal,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
