import { useRef, useContext, useEffect } from 'react';

import { ReactComponent as ShoppingIcon } from '../../assets/shopping-bag.svg';
import { CartContext } from '../../contexts/cart.context';

import { CartIconContainer, ItemCount } from './cart-icon.styles';

const CartIcon = () => {
  const { isCartOpen, setIsCartOpen, cartCount } = useContext(CartContext);

  const toggleIsCartOpen = () => setIsCartOpen(!isCartOpen);

  // Get a reference to the element that the dropdown is contained in
  const dropdownRef = useRef();

  // Add an event listener to the document object that listens for clicks
  useEffect(() => {
    const handleClick = (event) => {
      // Check if the element that was clicked is the same as the element that the dropdown is contained in
      if (!dropdownRef.current.contains(event.target)) {
        // If it is not, then close the dropdown
        setIsCartOpen(false);
      }
    };

    document.addEventListener('click', handleClick);

    return () => {
      // Remove the event listener when the component unmounts
      document.removeEventListener('click', handleClick);
    };
  }, []);

  return (
    <CartIconContainer onClick={toggleIsCartOpen} ref={dropdownRef}>
      <ShoppingIcon className='shopping-icon' />
      <ItemCount>{cartCount}</ItemCount>
    </CartIconContainer>
  );
};

export default CartIcon;
